$(document).ready(function(){var t=0;$(window).scroll(function(){var n=$("#numeros").offset().top-window.innerHeight;0==t&&$(window).scrollTop()>n&&($(".numero").each(function(){var t=$(this),n=t.attr("data-count");$({countNum:t.text()}).animate({countNum:n},{duration:2e3,easing:"swing",step:function(){t.text(Math.floor(this.countNum))},complete:function(){t.text(this.countNum)}})}),t=1)})});